INSERT INTO `blogmaster` VALUES ('32', 'c001', '1', '发展理念', '智行无疆，创值无限', '<p style=\"text-align: justify\">\r\n	<span style=\"font-size: 12px\"><img alt=\"\" src=\"http://web337788.host89.chinajsp.net/images/1293253291847.jpg\" /></span><br />\r\n	<br />\r\n	<span style=\"font-size: 12px\">合城设计经过多年的发展，逐渐明确了自身的设计哲学：集合智慧，创造价值。设计是思想灵感的体现，也是缔造价值的方法。</span></p>\r\n<p style=\"text-align: justify\">\r\n	<span style=\"font-size: 12px\">合城设计提倡的设计文化是：智行无疆，创值无限&mdash;&mdash;智慧跨越疆界，设计创造价值。</span></p>\r\n<p style=\"text-align: justify\">\r\n	<span style=\"font-size: 12px\">合城设计积极而开放，融会智慧、联手世界、共创价值&mdash;&mdash;通过科学技术，构建人文之美，是我们永恒的追求。</span></p>\r\n<p style=\"text-align: justify\">\r\n	<span style=\"font-size: 12px\">我们愿与智者同行，为创造更美好的家园而努力奉献！</span><br />\r\n	<br />\r\n	&nbsp;</p>\r\n', '2010-12-04', null, null, '2010-12-30', '0');
